SELECT ST_CoveredBy(smallc,smallc) AS small_coveredBy_small, ST_CoveredBy(smallc, bigc) AS small_coveredby_big, ST_CoveredBy(bigc, smallc) as big_coveredby_small, ST_CoveredBy(ST_ExteriorRing(bigc), bigc) as exterior_coveredby_big, ST_Within(ST_ExteriorRing(bigc),bigc) as exterior_within_big FROM (SELECT ST_Buffer(ST_GeomFromText('POINT(1 2)'), 10) As smallc, ST_Buffer(ST_GeomFromText('POINT(1 2)'), 20) As bigc) As foo;
